let startX = input[0].findIndex(e => e === '.');
let startY = 0;
let goalX = input[input.length - 1].findIndex(e => e === '.');
let goalY = input.length - 1;

const buildMap = () => {
    const map = {};
    const blizzards = [];
    const blizzardObjects = ['^', 'v', '>', '<'];
    for (let y = 0; y < input.length; y++) {
        for (let x = 0; x < input[y].length; x++) {
            const c = input[y][x];
            if (blizzardObjects.includes(c)) {
                map[`${x},${y}`] = c;
                blizzards.push({
                    x,
                    y,
                    type: c
                });
            }
            if (c === '#') map[`${x},${y}`] = c;
        }
    }
    return {
        map,
        blizzards
    };
};

const eligibleMoves = (state) => {
    const { x, y, map } = state;
    let topX = (goalY === 0) ? goalX : startX;
    let bottomX = (goalY === 0) ? startX : goalX;
    const eligible = [];
    if (x > 1 && !map[`${x - 1},${y}`]) eligible.push([x - 1, y, 'left']);
    if (x < input[0].length - 2 && !map[`${x + 1},${y}`]) eligible.push([x + 1, y, 'right']);
    if ((y > 1 || (y === 1 && x === topX)) && !map[`${x},${y - 1}`]) eligible.push([x, y - 1, 'up']);
    if ((y < input.length - 2 || (y === input.length - 2 && x === bottomX)) && !map[`${x},${y + 1}`]) eligible.push([x, y + 1, 'down']);
    return eligible;
};

const cloneState = (state) => {
    const newState = {
        x: state.x,
        y: state.y,
        moves: state.moves,
        map: {},
        blizzards: [],
        history: [],
    };
    for (const b of state.blizzards){
        newState.blizzards.push({...b});
    }
    for (const h of state.history){
        newState.history.push(h);
    }
    for (const [k,v] of Object.entries(state.map)){
        newState.map[k] = v;
    }
    return newState;
};

const drawMap = (state) => {
    const { x: pX, y: pY, map } = state;
    const lines = [];
    for (let y = 0; y < input.length; y++) {
        let line = '';
        for (let x = 0; x < input[y].length; x++) {
            if (pX === x && pY === y) {
                line += 'X';
            } else {
                line += (map[`${x},${y}`]) ? map[`${x},${y}`] : '.';
            }
        }
        lines.push(line);
    }
    console.log('\n\n');
    for (const line of lines) console.log(line);
};

